mapper.select:
select concat('${username}',replace(code,substring(code,length(code)-2),''),'s',substring(code,length(code)-2),'.txt')
from (

select  IF(length(code)=4, concat('00',code), IF(length(code)=5, concat('0',code),code)) as code  from(
select  MAX(CAST(e AS UNSIGNED)) AS code from (select replace(c,'.txt','') as e from (select substring(a,length('${username}')+1) as c from(select replace(RJS8,'s','') as a from MAIN where rjs8 like '%${username}%') as b) as d) as f
) as g

) as h
